import { Component, Output } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  isAdminPageActive: boolean = false;

}
<div class="container">
  <div class="row">
    <app-navbar (isCurrentPageAdmin)="isAdminPageActive = $event"></app-navbar>
    <app-user-data-list *ngIf="!isAdminPageActive"></app-user-data-list>
    <app-admin-list *ngIf="isAdminPageActive"></app-admin-list>
  </div>
</div>
