import { Component, OnInit } from '@angular/core';
import { UserData } from '../userData.model';
import { UserDataService } from '../user-data.service';
import { FormBuilder, FormGroup } from '@angular/forms';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-user-data-list',
  templateUrl: './user-data-list.component.html',
  styleUrls: ['./user-data-list.component.css']
})
export class UserDataListComponent implements OnInit {
  userDataList: UserData[] = [];
  newRecordForm: FormGroup;
  private apiBaseUrl = 'http://localhost:5000';
  editMode: boolean = false; // Add this property

  constructor(private userDataService: UserDataService, private formBuilder: FormBuilder, private http: HttpClient) {
    this.initializeForm();
  }

  initializeForm() {
    this.newRecordForm = this.formBuilder.group({
      begin: [''], // Add default values here or leave empty
      end: [''],
      dateInfo: [''],
      excuse: [''],
      excuseHours: [null],
      timeout: [null]
    });
  }

  ngOnInit(): void {
    this.fetchUserData(); // Fetch initial data when the component initializes
  }

  removeRecord(record: UserData) {
    const idToDelete = record.id; // Assuming you have an 'id' property in your UserData model
    if (!idToDelete) {
      return;
    }

    // Make an HTTP DELETE request to delete the record
    this.http.delete(`${this.apiBaseUrl}/deleteUserData/${idToDelete}`).subscribe(
      () => {
        console.log('User data deleted successfully');
        this.fetchUserData(); // Fetch data after a successful deletion
      },
      (error) => {
        console.error('Failed to delete user data:', error);
      }
    );
  }

  logFormValues() {
    const formValues = this.newRecordForm.value;
    console.log('Form Values:', formValues);

    const userData = {
      agentId: "glb901",
      firstName: "Asrin",
      lastName: "Yilmaz",
      begin: formValues.begin,
      end: formValues.end,
      dateInfo: formValues.dateInfo,
      excuse: formValues.excuse,
      excuseHours: formValues.excuseHours,
      timeout: formValues.timeout,
    };

    console.log('UserData:', userData);

    this.http.post(`${this.apiBaseUrl}/addUserData`, userData).subscribe(
      (response) => {
        console.log('User data added successfully:', response);
        this.fetchUserData(); // Fetch data after a successful addition
      },
      (error) => {
        console.error('Failed to add user data:', error);
      }
    );
  }

  onSubmit() {
    this.logFormValues();
  }

  fetchUserData() {
    // Fetch the list of data from your service
    this.userDataService.getRecordsByAgent().subscribe((data) => {
      this.userDataList = data;
    });
  }

  selectedIndex: number | null = null; // Add this property

  editRecord(index: number) {
    this.editMode = !this.editMode;
    if (this.editMode) {
      this.selectedIndex = index;
    } else {
      this.selectedIndex = null;
    }
  }
}
<div class="container">
  <div class="row">
    <div class="col-xs-12">
      <h3>Add New Record</h3>
      <form (ngSubmit)="onSubmit()" [formGroup]="newRecordForm">
        <div class="row">
          <div class="col-xs-2">
            <div class="form-group">
              <label for="begin">Begin:</label>
              <input type="text" class="form-control" id="begin" formControlName="begin" placeholder="Enter Begin">
            </div>
          </div>
          <div class="col-xs-2">
            <div class="form-group">
              <label for="end">End:</label>
              <input type="text" class="form-control" id="end" formControlName="end" placeholder="Enter End">
            </div>
          </div>
          <div class="col-xs-2">
            <div class="form-group">
              <label for="dateInfo">Date Info:</label>
              <input type="text" class="form-control" id="dateInfo" formControlName="dateInfo" placeholder="Enter Date Info">
            </div>
          </div>
          <div class="col-xs-2">
            <div class="form-group">
              <label for="excuse">Excuse:</label>
              <input type="text" class="form-control" id="excuse" formControlName="excuse" placeholder="Enter Excuse">
            </div>
          </div>
          <div class="col-xs-2">
            <div class="form-group">
              <label for="excuseHours">Excuse Hours:</label>
              <input type="number" class="form-control" id="excuseHours" formControlName="excuseHours" placeholder="Enter Excuse Hours">
            </div>
          </div>
          <div class="col-xs-2">
            <div class="form-group">
              <label for="timeout">Timeout:</label>
              <input type="number" class="form-control" id="timeout" formControlName="timeout" placeholder="Enter Timeout">
            </div>
          </div>
        </div>
        <div class="col-xs-12 text-center">
          <button type="submit" class="btn btn-primary">Submit</button>
        </div>
      </form>
    </div>
  </div>
</div>

<div class="container">
  <table class="table table-bordered">
    <thead>
      <tr>
        <th>Begin</th>
        <th>End</th>
        <th>Date Info</th>
        <th>Excuse</th>
        <th>Excuse Hours</th>
        <th>Timeout</th>
        <th>Action</th> <!-- For Edit and Remove Buttons -->
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let record of userDataList; let i = index">
        <td>
          <ng-container *ngIf="editMode && i === selectedIndex; else viewMode">
            <input type="text" [(ngModel)]="record.editBegin">
          </ng-container>
          <ng-template #viewMode>{{ record.begin }}</ng-template>
        </td>
        <td>
          <ng-container *ngIf="editMode && i === selectedIndex; else viewMode">
            <input type="text" [(ngModel)]="record.editEnd">
          </ng-container>
          <ng-template #viewMode>{{ record.end }}</ng-template>
        </td>
        <td>
          <ng-container *ngIf="editMode && i === selectedIndex; else viewMode">
            <input type="text" [(ngModel)]="record.editDateInfo">
          </ng-container>
          <ng-template #viewMode>{{ record.dateInfo }}</ng-template>
        </td>
        <td>
          <ng-container *ngIf="editMode && i === selectedIndex; else viewMode">
            <input type="text" [(ngModel)]="record.editExcuse">
          </ng-container>
          <ng-template #viewMode>{{ record.excuse }}</ng-template>
        </td>
        <td>
          <ng-container *ngIf="editMode && i === selectedIndex; else viewMode">
            <input type="number" [(ngModel)]="record.editExcuseHours">
          </ng-container>
          <ng-template #viewMode>{{ record.excuseHours }}</ng-template>
        </td>
        <td>
          <ng-container *ngIf="editMode && i === selectedIndex; else viewMode">
            <input type="number" [(ngModel)]="record.editTimeout">
          </ng-container>
          <ng-template #viewMode>{{ record.timeout }}</ng-template>
        </td>
        <td>
          <button class="btn btn-primary" (click)="editRecord(i)">
            <i class="fas fa-edit"></i> <!-- Edit Icon -->
          </button>
          <button class="btn btn-danger" (click)="removeRecord(record)">
            <i class="fas fa-trash-alt"></i> <!-- Remove Icon -->
          </button>
        </td>
      </tr>
    </tbody>
  </table>
</div>
