import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms'; // Import necessary form-related modules

@Component({
  selector: 'app-add-user-data',
  templateUrl: './add-user-data.component.html',
  styleUrls: ['./add-user-data.component.css']
})
export class AddUserDataComponent implements OnInit {
  newRecordForm: FormGroup; // Declare the form group

  constructor(private formBuilder: FormBuilder) {}

  ngOnInit(): void {
    this.initializeForm(); // Call the method to initialize the form
  }

  // Method to initialize the form and its controls
  initializeForm() {
    this.newRecordForm = this.formBuilder.group({
      begin: ['', Validators.required],
      end: ['', Validators.required],
      dateInfo: ['', Validators.required],
      excuse: ['', Validators.required],
      excuseHours: ['', Validators.required],
      timeout: ['', Validators.required],
    });
  }

  // Method to handle form submission
  addRecord() {
    if (this.newRecordForm.valid) {
      const newRecordData = this.newRecordForm.value;
      // Send newRecordData to your API for insertion
      // Implement API call logic here
      console.log(newRecordData); // For demonstration, log the data to the console
    }
  }
}
