import { Component } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { HttpClient } from '@angular/common/http'; // Import HttpClient

@Component({
  selector: 'app-add-user-data',
  templateUrl: './add-user-data.component.html',
  styleUrls: ['./add-user-data.component.css']
})
export class AddUserDataComponent {
  newRecordForm: FormGroup;
  private apiBaseUrl = 'http://localhost:5000'; // Update with your Flask API URL

  constructor(private formBuilder: FormBuilder, private http: HttpClient) {
    this.initializeForm();
  }

  initializeForm() {
    this.newRecordForm = this.formBuilder.group({
      // Define your form controls here
    });
  }

  logFormValues() {
    const formValues = this.newRecordForm.value;
    console.log('Form Values:', formValues);

    // Create a UserData object and populate its properties
    const userData = {
      agentId: "glb901",
      firstName: "Asrin",
      lastName: "Yilmaz",
      begin: formValues.begin,
      end: formValues.end,
      dateInfo: formValues.dateInfo,
      excuse: formValues.excuse,
      excuseHours: formValues.excuseHours,
      timeout: formValues.timeout,
    };

    console.log('UserData:', userData);

    // Send a POST request to your Flask API
    this.http.post(`${this.apiBaseUrl}/addUserData`, userData).subscribe(
      (response) => {
        console.log('User data added successfully:', response);
      },
      (error) => {
        console.error('Failed to add user data:', error);
      }
    );
  }

  onSubmit() {
    // Handle form submission (e.g., send data to the server)
  }
}
