import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient } from '@angular/common/http'; // Import HttpClient

@Component({
  selector: 'app-add-user-data',
  templateUrl: './add-user-data.component.html',
  styleUrls: ['./add-user-data.component.css']
})
export class AddUserDataComponent implements OnInit {
  newRecordForm: FormGroup; // Declare the form group

  private apiBaseUrl = 'http://localhost:5000'; // Update with your Flask API URL

  constructor(
    private formBuilder: FormBuilder,
    private http: HttpClient // Inject HttpClient
  ) {}

  ngOnInit(): void {
    this.initializeForm();
  }

  initializeForm() {
    this.newRecordForm = this.formBuilder.group({
      begin: ['', Validators.required],
      end: ['', Validators.required],
      dateInfo: ['', Validators.required],
      excuse: ['', Validators.required],
      excuseHours: ['', Validators.required],
      timeout: ['', Validators.required],
    });
  }

  addRecord() {
    if (this.newRecordForm.valid) {
      const newRecordData = this.newRecordForm.value;
      // Send the new record data to your Flask API
      this.http.post(`${this.apiBaseUrl}/addUserData`, newRecordData).subscribe(
        (response) => {
          console.log('User data added successfully:', response);
        },
        (error) => {
          console.error('Failed to add user data:', error);
        }
      );
    }
  }
}
