import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { HttpClient } from '@angular/common/http';

import { UserData } from '../userData.model';
import { UserDataService } from '../user-data.service';

@Component({
  selector: 'app-user-data-list',
  templateUrl: './user-data-list.component.html',
  styleUrls: ['./user-data-list.component.css']
})
export class UserDataListComponent implements OnInit {
  userDataList: UserData[] = [];
  newRecordForm: FormGroup;
  editMode: boolean = false;
  editedRecord: UserData | null = null;
  selectedIndex: number | null = null;
  private apiBaseUrl = 'http://localhost:5000';

  constructor(
    private userDataService: UserDataService,
    private formBuilder: FormBuilder,
    private http: HttpClient
  ) {
    this.initializeForm();
  }

  initializeForm() {
    this.newRecordForm = this.formBuilder.group({
      begin: [''],
      end: [''],
      dateInfo: [''],
      excuse: [''],
      excuseHours: [null],
      timeout: [null]
    });
  }

  ngOnInit(): void {
    this.fetchUserData();
  }

  editRecord(index: number) {
    if (this.editMode) {
      // Save the edited record and exit edit mode
      this.saveEditedRecord();
    } else {
      // Enter edit mode and set the editedRecord and selectedIndex
      this.editMode = true;
      this.editedRecord = { ...this.userDataList[index] };
      this.selectedIndex = index;
    }
  }

  saveEditedRecord() {
    if (this.editedRecord && this.selectedIndex !== null) {
      // Update the record in the list
      this.userDataList[this.selectedIndex] = { ...this.editedRecord };

      // Reset edit mode, editedRecord, and selectedIndex
      this.editMode = false;
      this.editedRecord = null;
      this.selectedIndex = null;
    }
  }

  removeRecord(record: UserData) {
    // Implement remove logic here
  }

  logFormValues() {
    const formValues = this.newRecordForm.value;
    console.log('Form Values:', formValues);

    const userData = {
      agentId: 'glb901',
      firstName: 'Asrin',
      lastName: 'Yilmaz',
      begin: formValues.begin,
      end: formValues.end,
      dateInfo: formValues.dateInfo,
      excuse: formValues.excuse,
      excuseHours: formValues.excuseHours,
      timeout: formValues.timeout,
    };

    console.log('UserData:', userData);

    this.http.post(`${this.apiBaseUrl}/addUserData`, userData).subscribe(
      (response) => {
        console.log('User data added successfully:', response);
        this.fetchUserData();
      },
      (error) => {
        console.error('Failed to add user data:', error);
      }
    );
  }

  onSubmit() {
    if (this.editMode) {
      // Save the edited record and exit edit mode
      this.saveEditedRecord();
    } else {
      this.logFormValues();
      this.newRecordForm.reset();
    }
  }

  fetchUserData() {
    this.userDataService.getRecordsByAgent().subscribe((data) => {
      this.userDataList = data;
    });
  }
}
